// export const LOCAL_HOST = 'http://172.31.249.212:992';
export const LOCAL_HOST = 'http://localhost:8081'

export const GET_DEVICES_LOGIN =(login) => `${LOCAL_HOST}/api/devices/myDevices/${login}`;
export const GET_CONFIG_LOGIN =(login) => `${LOCAL_HOST}/api/devices/configurations/${login}`;

export const LOGIN_USER = LOCAL_HOST + '/api/users/loginUser';
export const GET_ALL_CONFIGURATIONS = LOCAL_HOST + '/api/configurations/all';
export const ADD_CONFIGURATION = LOCAL_HOST+'/api/configurations';
export const UPDATE_CONFIGURATION = (configId) => `${LOCAL_HOST}/api/configurations/${configId}`;
export const GET_ALL_DEVICES = LOCAL_HOST + '/api/devices/all';

export const loginUser = async (login, password) => {
    try {
        const response = await fetch(LOGIN_USER, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                login: login,
                password: password
            }),
        });

        if (!response.ok) {
            const errorText = await response.text();
            throw new Error(`Error: ${errorText}`);
        }

        const data = await response.json();
        return data;
    } catch (error) {
        console.error('Login failed:', error.message);
        throw error;
    }
};
