import React, { useState, useEffect } from 'react';
import { GET_DEVICES_LOGIN, ADD_CONFIGURATION } from '../constants/back';
import '../styles/ConfigurationForm.css';

const ConfigurationForm = () => {
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState('');
    const [configName, setConfigName] = useState('');
    const [mode, setMode] = useState('');
    const [schedule, setSchedule] = useState('');
    const [energySaving, setEnergySaving] = useState(false);
    const [popup, setPopup] = useState('');
    const login = localStorage.getItem('username');

    useEffect(() => {
        const fetchDevices = async () => {
            try {
                const response = await fetch(GET_DEVICES_LOGIN(login));
                const data = await response.json();
                setDevices(data);
            } catch (error) {
                console.error('Error fetching devices:', error);
            }
        };
        fetchDevices();
    }, [login]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!selectedDevice) {
            setPopup('Please select a device.');
            return;
        }

        const configData = {
            configurationName: configName,
            mode,
            schedule,
            energySaving,
            deviceId: selectedDevice,
        };

        try {
            const response = await fetch(ADD_CONFIGURATION, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(configData),
            });

            if (response.status === 409) {
                setPopup('This device already has a configuration. Please update it.');
            } else if (response.ok) {
                setPopup('Configuration added successfully!');
            } else {
                setPopup('Failed to add configuration.');
            }
        } catch (error) {
            setPopup('An error occurred while adding the configuration.');
            console.error('Error:', error);
        }
    };

    return (
        <div className="config-form-container">
            <h1>Add Configuration</h1>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Configuration Name</label>
                    <input
                        type="text"
                        value={configName}
                        onChange={(e) => setConfigName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Mode</label>
                    <input
                        type="text"
                        value={mode}
                        onChange={(e) => setMode(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Schedule</label>
                    <input
                        type="text"
                        value={schedule}
                        onChange={(e) => setSchedule(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Energy Saving</label>
                    <input
                        type="checkbox"
                        checked={energySaving}
                        onChange={() => setEnergySaving(!energySaving)}
                    />
                </div>

                <div className="form-group">
                    <label>Select Device</label>
                    <select
                        value={selectedDevice}
                        onChange={(e) => setSelectedDevice(e.target.value)}
                        required
                    >
                        <option value="">Select a device</option>
                        {devices.map((device) => (
                            <option key={device.id} value={device.id}>
                                {device.name}
                            </option>
                        ))}
                    </select>
                </div>

                <button type="submit" className="submit-btn">Add Configuration</button>
            </form>

            {popup && (
                <div className="popup">
                    <p>{popup}</p>
                    <button onClick={() => setPopup('')}>Close</button>
                </div>
            )}
        </div>
    );
};

export default ConfigurationForm;
