import React, { useState, useEffect } from 'react';
import '../styles/DeviceList.css';
import { GET_DEVICES_LOGIN } from '../constants/back';

const DeviceList = ({ login }) => {
    const [devices, setDevices] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchDevices = async () => {
            try {
                const response = await fetch(GET_DEVICES_LOGIN(login));

                // Vérifier si la réponse est OK (status 200)
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.error || 'Failed to fetch devices');
                }

                const data = await response.json();
                setDevices(data);
            } catch (err) {
                setError(err.message);
            }
        };

        fetchDevices();
    }, [login]);

    return (
        <div className="device-list-container">
            <h2>My Devices</h2>

            {error ? (
                <p className="error-message">{error}</p>
            ) : (
                <table className="device-table">
                    <thead>
                    <tr>
                        <th>Name</th>
                        <th>Type</th>
                        <th>Status</th>
                    </tr>
                    </thead>
                    <tbody>
                    {devices.length > 0 ? (
                        devices.map((device) => (
                            <tr key={device.id}>
                                <td>{device.name}</td>
                                <td>{device.type}</td>
                                <td>{device.status}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="3">No devices found</td>
                        </tr>
                    )}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default DeviceList;
