import React, { useEffect, useState } from 'react';
import '../styles/Login.css';
import { useNavigate } from 'react-router-dom';
import { loginUser } from "../constants/back";  // Import de la méthode de connexion depuis back.js

const Login = () => {
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    let navigate = useNavigate();

    useEffect(() => {
        document.body.classList.add('login-page');
        return () => {
            document.body.classList.remove('login-page');
        };
    }, []);

    // Méthode de connexion unique
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const userData = await loginUser(login, password);
            console.log('User logged in:', userData);
            localStorage.setItem('username', userData.login);
            navigate('*');
        } catch (error) {
            setError('Invalid username or password');
        }
    };

    return (
        <div className="login-page-container">
            <div className="login-background">
                <div className="login-container">
                    <form className="login-form" onSubmit={handleLogin}>
                        <h2>Login</h2>
                        {error && <div className="error-message">{error}</div>}
                        <input
                            type="text"
                            placeholder="Username"
                            value={login}
                            onChange={(e) => setLogin(e.target.value)}
                            required
                        />
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <button type="submit">
                            Login
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
