import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GET_CONFIGS_BY_FULLNAME } from '../constants/back';
import '../styles/ConfigurationList.css';
import { FaExclamationCircle } from 'react-icons/fa';

const ConfigurationList = () => {
    const [configurations, setConfigurations] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    const navigate = useNavigate();
    const fullName = localStorage.getItem('fullName');  // Récupération de fullName depuis localStorage

    useEffect(() => {
        const fetchConfigurations = async () => {
            if (!fullName) {
                setError('User full name not provided');
                setLoading(false);
                return;
            }

            try {
                const response = await fetch(GET_CONFIGS_BY_FULLNAME(fullName));

                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.error || 'Failed to fetch configurations');
                }

                const data = await response.json();
                setConfigurations(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchConfigurations();
    }, [fullName]);

    return (
        <div className="device-list-container">
            <h1>My Configurations</h1>

            {loading && <p>Loading configurations...</p>}

            {error && (
                <div className="error-message">
                    <FaExclamationCircle /> {error}
                </div>
            )}

            {!loading && configurations.length === 0 && !error && (
                <p>No configurations found for user <strong>{fullName}</strong>.</p>
            )}

            <table className="device-table">
                <thead>
                <tr>
                    <th>Configuration Name</th>
                    <th>Energy Saving</th>
                    <th>Mode</th>
                    <th>Schedule</th>
                </tr>
                </thead>
                <tbody>
                {configurations.map((config) => (
                    <tr key={config.id}>
                        <td>{config.configurationName}</td>
                        <td>{config.energySaving ? 'Yes' : 'No'}</td>
                        <td>{config.mode}</td>
                        <td>{config.schedule}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default ConfigurationList;
