import React, { useState, useEffect } from 'react';
import { GET_DEVICES_LOGIN, ADD_CONFIGURATION } from '../constants/back';
import '../styles/ConfigurationForm.css';
import { FaPlus } from 'react-icons/fa';

const ConfigurationForm = () => {
    const [devices, setDevices] = useState([]);
    const [selectedDevice, setSelectedDevice] = useState('');
    const [mode, setMode] = useState('Automatic');
    const [energySaving, setEnergySaving] = useState(false);
    const [schedule, setSchedule] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const login = localStorage.getItem('username');

    useEffect(() => {
        const fetchDevices = async () => {
            try {
                const response = await fetch(GET_DEVICES_LOGIN(login));
                const data = await response.json();
                setDevices(data);
            } catch (err) {
                setError('Failed to fetch devices.');
            }
        };

        fetchDevices();
    }, [login]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!selectedDevice || !schedule) {
            setError('All fields are required.');
            return;
        }

        const configuration = {
            configurationName: `${selectedDevice} Config`,
            mode,
            energySaving,
            schedule
        };

        try {
            const response = await fetch(ADD_CONFIGURATION(selectedDevice), {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(configuration)
            });

            if (response.ok) {
                setSuccess('Configuration added successfully!');
            } else {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to add configuration.');
            }
        } catch (err) {
            setError(err.message);
        }
    };

    const handleSwitchToggle = () => {
        setEnergySaving(!energySaving);
    };

    return (
        <div className="config-form-container">
            <h1>Add Configuration</h1>
            <form onSubmit={handleSubmit}>
                {/* Select Device */}
                <div className="form-group">
                    <label>Device</label>
                    <select value={selectedDevice} onChange={(e) => setSelectedDevice(e.target.value)}>
                        <option value="">Select Device</option>
                        {devices.map((device) => (
                            <option key={device.id} value={device.id}>
                                {device.name}
                            </option>
                        ))}
                    </select>
                </div>

                {/* Select Mode */}
                <div className="form-group">
                    <label>Mode</label>
                    <select value={mode} onChange={(e) => setMode(e.target.value)}>
                        <option value="Automatic">Automatic</option>
                        <option value="Manual">Manual</option>
                    </select>
                </div>

                {/* Energy Saving Switch */}
                <div className="form-group switch-group">
                    <label>Energy Saving</label>
                    <div className={`switch ${energySaving ? 'active' : ''}`} onClick={handleSwitchToggle}>
                        <div className="slider"></div>
                    </div>
                </div>

                {/* Schedule Selection */}
                <div className="form-group">
                    <label>Schedule</label>
                    <input
                        type="text"
                        placeholder="08:00-18:00"
                        value={schedule}
                        onChange={(e) => setSchedule(e.target.value)}
                    />
                </div>

                {error && <p className="error-message">{error}</p>}
                {success && <p className="success-message">{success}</p>}

                <button type="submit" className="add-button">
                    <FaPlus /> Add Configuration
                </button>
            </form>
        </div>
    );
};

export default ConfigurationForm;
