import React, { useEffect, useState } from 'react';
import '../styles/Login.css';
import { useNavigate } from 'react-router-dom';
import { loginAdmin, loginAccountant, loginCustomer } from "../constants/back";

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    let navigate = useNavigate();

    useEffect(() => {
        document.body.classList.add('login-page');
        return () => {
            document.body.classList.remove('login-page');
        };
    }, []);

    const handleLogin = async (loginFunction, redirectPath) => {
        try {
            const userData = await loginFunction(username, password);
            console.log('User logged in:', userData);
            localStorage.setItem('username', username);
            navigate(redirectPath);
        } catch (error) {
            setError('Invalid username or password');
        }
    };

    return (
        <div className="login-page-container">
            <div className="login-background">
                <div className="login-container">
                    <form className="login-form">
                        <h2>Login</h2>
                        {error && <div className="error-message">{error}</div>}
                        <input
                            type="text"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <button
                            onClick={(e) => {
                                e.preventDefault();
                                handleLogin(loginAccountant, '/DunningSubscription');
                            }}
                        >
                            Login as Accountant
                        </button>
                        <button
                            onClick={(e) => {
                                e.preventDefault();
                                handleLogin(loginAdmin, '/invoice');
                            }}
                        >
                            Login as Admin
                        </button>
                        <button
                            onClick={(e) => {
                                e.preventDefault();
                                handleLogin(loginCustomer, '/invoiceClient');
                            }}
                        >
                            Login as Customer
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Login;
